const brown = chalk.hex('#8B4513');

/**
 * Print the Last Supper banner + FORGE title + subtitle
 */
export function showBanner(savedCount?: number) {
  console.log('');

  // Frame top
  console.log(gold('  ╔' + '═'.repeat(76) + '╗'));

  // Painting rows inside frame
  for (const line of PAINTING_LINES) {
    console.log(gold('  ║') + line + gold('║'));
  }

  // Frame bottom
  console.log(gold('  ╚' + '═'.repeat(76) + '╝'));

  console.log('');
  console.log(
    amber('         ███████╗ ██████╗ ██████╗  ██████╗ ███████╗')
  );
  console.log(
    amber('         ██╔════╝██╔═══██╗██╔══██╗██╔════╝ ██╔════╝')
  );
  console.log(
    gold('         █████╗  ██║   ██║██████╔╝██║  ███╗█████╗  ')
  );
  console.log(
    darkGold('         ██╔══╝  ██║   ██║██╔══██╗██║   ██║██╔══╝  ')
  );
  console.log(
    brown('         ██║     ╚██████╔╝██║  ██║╚██████╔╝███████╗')
  );
  console.log(
    brown('         ╚═╝      ╚═════╝ ╚═╝  ╚═╝ ╚═════╝ ╚══════╝')
  );
  console.log('');
  console.log(
    warmWhite('              The Digital Renaissance of Ideas')
  );
  console.log(
    chalk.dim('           "Where great minds converge, renaissance begins"')
  );
  console.log('');

  if (savedCount && savedCount > 0) {
    console.log(chalk.dim(`              ${savedCount} saved session(s) available`));
    console.log('');
  }
}